/* CSS */
.print-format {
  padding: 18px;
  font-family: Raleway;
}
h3 {
  width: 100%; 
  text-align: center; 
  border-bottom: 3px solid #abacad; 
  line-height: 0.6em;
  margin: 3px 0 3px; 
} 
h3 span { 
  background:#fff; 
  padding:0 10px; 
}
#container {
  height: 240px;
  line-height: 240px;
}
#container img {
  vertical-align: middle;
}
