css:
#header {
    text-align: right;
    width: 100%;
    /* ( 45 mm - 10 mm margin ) */
    height: 35mm;
    /* Overwrite empty header inserted by frappe */
    margin-top: -10mm;
    overflow: hidden;
    padding-left: 32.25mm;
    padding-right: 25.8mm;
    position: relative;
}

#sender {
    position: relative;
    font-size: 75%;
    height: 17.7mm;
}

#sender p {
    position: absolute;
    bottom: 0;
}

#address {
    float: left;
    width: 85mm;
    padding-left: 5mm;
    margin-left: 20mm;
    height: 45mm;
    overflow: hidden;
}

#contact {
    width: 75mm;
    height: 40mm;
    overflow: hidden;
    margin-top: 5mm;
    margin-left: 20mm;
}

#contact>table {
    float: right;
    width: 100%;
}

#subject {
    margin-bottom: 2em;
    height: 5mm;
    font-weight: bold;
}

#faltmarke-1 {
    position: absolute;
    /* ( 105 mm - 10 mm margin ) */
    top: 95mm;
}

#lochmarke {
    position: absolute;
    /* ( 148.5 mm - 10 mm margin ) */
    top: 138.5mm;
}

#faltmarke-2 {
    position: absolute;
    /* ( 210 mm - 10 mm margin ) */
    top: 200mm;
}

#text {
    margin-top: 8.46mm;
    margin-left: 25mm;
    margin-right: 20mm;
}

#contact td {
    padding: 1.5px !important;
}

.text-right {
    text-align: right;
}

/* --- Nice page breaks for tables --- */
table {
    page-break-inside: auto
}

tr {
    page-break-inside: avoid;
    page-break-after: auto
}

thead {
    display: table-header-group
}

tfoot {
    display: table-footer-group
}
/* ---------------------------------- */

/* ------------- Footer ------------- */
#pagenum {
    text-align: right;
    margin-right: 20mm;
    margin-top: 4.23mm;
    margin-bottom: 4.23mm;
}

.footer-table>tbody>tr>td:not(:last-child) {
    padding: 0 30px 0 0 !important;

}

.footer-table>tbody>tr>td:last-child {
    padding: 0 !important;
}

.footer-table {
    font-size: 10pt;
    margin-left: 25mm;
    padding-right: 20mm;
}
/* ---------------------------------- */
